#include<vector>
#include<map>
#include<iostream>
#include<algorithm>
using namespace std;

class Solution {
    map<string,int> dmap;
public:
    vector<vector<int>> twosum(vector<int>& nums,int& target){
        vector<vector<int>> results;
        int i=0;
        int j=nums.size()-1;
        while(i<j){
            int sum = nums[i]+nums[j];
            if(sum > target) j--;
            else if(sum<target) i++;
            else{
                vector<int> result;
                result.push_back(nums[i]);
                result.push_back(nums[j]);
                results.push_back(result);
                i++;
                j--;
            }
        }
        return results;
        
    }
 
    string tostring(vector<int>& nums){
        string str;
        for(auto num : nums){
            str+=to_string(num);
        }
        return str;
    }

    void outputs(std::vector<int>& outputs){
       for(auto output : outputs){
         std::cout<<output<<" ";
       }
       std::cout<<std::endl;
    
    }

    void deduplicate(vector<int>& nums){
      int last =nums[0];
      int count=1;
      for(std::vector<int>::iterator it=nums.begin()+1; it!=nums.end();){
          if(last == *it){
             count++;
             if(count > 3){
               last=*it;
               it=nums.erase(it);
               continue;
             }
          }else{
             count=0;
          }
          last=*it;
          it++;
      }      


    }
    vector<vector<int>> threeSum(vector<int>& nums) {
        vector<vector<int>> solutions;
        sort(nums.begin(),nums.end());
        deduplicate(nums);
        cout<<"after"<<endl;
        outputs(nums);
        for(int i=0;i<nums.size();i++){
            int target=-nums[i];
            vector<int> tvector(nums);
            tvector.erase(tvector.begin()+i);
            vector<vector<int>> results=twosum(tvector,target);
            if(!results.empty()){
                for(auto result : results){
                result.push_back(nums[i]);
                cout<<tostring(result)<<"  index:  "<<i<<endl;
                sort(result.begin(),result.end());
                string str=tostring(result);
                if(dmap.find(str)==dmap.end()){
                    dmap[str]=1;
                    solutions.push_back(result);
                }
              }
            }
         }
        
        return solutions;
        
    }
};

